(()=>{"use strict";const e={submit:document.getElementById("formSubmit"),formInput:document.getElementById("formInput"),placeName:document.querySelector(".placeName"),currentTemp:document.querySelector(".currentTemp"),feelsLike:document.querySelector(".feelsLike")};e.submit.addEventListener("click",(()=>{!async function(e){try{const t=await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${e}&APPID=c20e51e1cd4bf0147fee570dd34d8dd1&units=metric&exclude=minutely,hourly,daily,alerts`),n=await t.json();(e=>{document.querySelector(".placeName").innerText=e.name,document.querySelector(".currentTemp").innerText=e.current,document.querySelector(".feelsLike").innerText=e.feelsLike})({name:n.name,current:n.main.temp,feelsLike:n.main.feels_like,minTemp:n.main.temp_min,maxTemp:n.main.temp_max,weatherMain:n.weather[0].main,weatherDescription:n.weather[0].description}),console.log("success")}catch{console.log("Ooops... there's an error")}}(e.formInput.value)}))})();